$(document).ready(function() {
    // Initialize Waypoints for each section
    $('.container').waypoint(function(direction) {
        if (direction === 'down') {
            // Show the button when scrolling down past a section
            $('#scrollToTop').fadeIn();
        } else {
            // Hide the button when scrolling up
            $('#scrollToTop').fadeOut();
        }
    }, {
        offset: '50%' // Trigger when the section is 50% visible
    });

    // Scroll to top functionality
    $('#scrollToTop').click(function() {
        $('html, body').animate({ scrollTop: 0 }, 'slow');
        return false;
    });
});
